import './ProjectMenu.css'
import * as React from 'react';
import PropTypes from 'prop-types';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import ProjectItem from './ProjectItem';



function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
      <div
        role="tabpanel"
        hidden={value !== index}
        id={`vertical-tabpanel-${index}`}
        aria-labelledby={`vertical-tab-${index}`}
        {...other}
      >
        {value === index && (
          <Box sx={{ p: 3 }}>
            <Typography>{children}</Typography>
          </Box>
        )}
      </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.number.isRequired,
    value: PropTypes.number.isRequired,
};

function a11yProps(index) {
    return {
        id: `vertical-tab-${index}`,
        'aria-controls': `vertical-tabpanel-${index}`,
    };
}



function ProjectMenu() {

    const projects = [
        {
            index: 0,
            title: "UAH QuietRoom",
            dates: "May 2023 - July 2023",
            url: "https://quietroom.app",
            details: [
                'Web application that finds UAH classroom availability for students to find study spots on campus',
                'Allows searching for schedule data for a specific room',
                'Displays room metadata including room number, room type, and capacity',
                'Generates schedule-view for selected room for readability',
                'Uses React.js with Next.js for front-end and .NET for back-end'
            ]
        },
        {
            index: 1,
            title: "Unscene",
            dates: "Aug 2022 - Dec 2022",
            url: "https://github.com/salsajeries/Unscene",
            details: [
                'Semester team project developing a desktop Java application with Apache Netbeans',
                'Movie and TV Show manager that allows creation of watchlists and progress dashboard of media as the user watches',
                'Pulls data from TheMovieDB API for all media'
            ]
        },
        {
            index: 2,
            title: "Adventure to the Sun",
            dates: "Oct 2022",
            url: "https://github.com/salsajeries/spaceapps-2022",
            details: [
                '48-hour simulative game that allows the user to play as the Parker Solar Probe on its mission',
                'Raw data from the solar probe is used to generate solar particles containing velocity and temperature, which the player must collect for points',
                '3D game developed in Godot game engine'
            ]
        },
        {
            index: 3,
            title: "Pomodoro Widget",
            dates: "May 2022 - Jun 2022",
            url: "https://salwajeries.com/pomodoro-notion/",
            details: [
                'Web application with built-in timer for Pomodoro study technique, task list, and Spotify playlist',
                'Responsive feedback for completion of tasks and study time',
                'Uses Bootstrap for GUI features',
                'Designed to be embedded as a Notion widget'
            ]
        },
        {
            index: 4,
            title: "SafeStay",
            dates: "Mar 2022",
            url: "https://github.com/salsajeries/GigaHurtzFrontEnd",
            details: [
                '48-hour web application that connects refugees with host families based on user preferences',
                'Compatability algorithm based on a point system generates a compatability score for match rankings'
            ]
        },
        {
            index: 5,
            title: "Notion Clock Widget",
            dates: "Jan 2022",
            url: "https://salwajeries.com/clockwidget-notion",
            details: [
                'Web application that displays date, time, and greeting message',
                'Allows for custom background and text color for personalization',
                'Designed to be embedded as a Notion widget'
            ]
        }
    ];

    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
      setValue(newValue);
    };

    return (
        <Box
            sx={{ flexGrow: 1, bgcolor: '#181848', display: 'flex', height: 224 }}
        >
            <Tabs
                orientation="vertical"
                variant="scrollable"
                value={value}
                onChange={handleChange}
                aria-label="Vertical tabs example"
                sx={{ borderRight: 1, borderColor: 'divider', width: '30%' }}
            >
                {projects.map((p) => {
                    return (
                        <Tab
                            label={p.title}
                            {...a11yProps(0)}
                        />
                    );
                })}
            </Tabs>
            {projects.map((p) => {
                return (
                    <TabPanel value={value} index={p.index} width='70%'>
                        <ProjectItem project={projects[0]} images={Portrait}></ProjectItem>
                    </TabPanel>
                );
            })}
        </Box>
    );
}

export default ProjectMenu;
